javascript:
let apps = [];
let run_names = [];
getAppName();
getRunName();
frappe.query_reports["Test Execution Statistics"] = {
    "filters": [{
        "fieldname": "app_name",
        "label": __("App"),
        "fieldtype": "Select",
        "default": '',
        "reqd": 1,
        "options": apps
    },
    {
        "fieldname": "run_name",
        "label": __("Test Run Name"),
        "fieldtype": "Select",
        "default": '',
        "reqd": 1,
        "options": run_names
    }
    ]
}

function getAppName() {
    apps = [];
    frappe.call({
        method: 'frappe.desk.reportview.get',
        args: {
            doctype: 'Module Def',
            fields: ["`tabModule Def`.`app_name`"],
            order_by: "`tabModule Def`.`modified` desc",
            start: 0,
            page_length: 2000,
            with_comment_count: true
        },
        freeze: true,
        freeze_message: 'Loading report please wait',
        async: false,
        callback: function (r) {
            if (!r.exc) {
                let all_apps = r.message.values;
                all_apps.forEach((a) => {
                    let app_name = a[0];
                    if (!apps.includes(app_name)) {
                        apps.push(app_name);
                    }
                });

                return apps;
            }
        }
    });
}

function getRunName() {
    run_names = [];
    frappe.call({
        type: 'GET',
        url: `${window.location.origin}/api/resource/Test Run Log?fields=["distinct test_run_name"]&limit_page_length=9000`,
        freeze: true,
        freeze_message: 'Loading report please wait',
        async: false,
        callback: function (r) {
            if (!r.exc) {
                let all_runs = r.data;
                all_runs.forEach((a) => {
                    let run_name = a['test_run_name'];
                    if (!run_names.includes(run_name)) {
                        run_names.push(run_name);
                    }
                });

                return run_names;
            }
        }
    });
}
